import React, {useState} from 'react';
import "./style.scss";

const images = [
    {
        src: 
        "",
        title: "Project 1",
        subtitle:"",
        category:"",
    },

    {
        src: 
        "",
        title: "Project 2",
        subtitle:"",
        category:"",
    },
    {
        src: 
        "",
        title: "Project 3",
        subtitle:"",
        category:"",
    },
    {
        src: 
        "",
        title: "Project 4",
        subtitle:"",
        category:"",
    },
];

function GalleryItem({src,category,subtitle,title,updateActiveImage,index}){
    return (
        <div className="gallery-item-wrapper">
            <div className="gallery-item">
                <div className="gallery-item-info">
                    <h1 className="gallery-item-title">{title}</h1>
                    <h6 className="gallery-item-subtitle">{subtitle}</h6>
                    <p className="gallery-item-category">{category}</p>
                
                </div>
                <div 
                    className="gallery-item-image"
                    style={{backgroundImage: 'url({src})'}}
                ></div>
            </div>
        </div>
    );
}

export default function Gallery() {
    const [activeImage,setActiveImage] = useState(1);
    return(
        <section className="gallery-wrap">
            <div className="gallery">
                <div className="gallery-counter">
 
                </div>
                {images.map((image,index) => (
                    <GalleryItem
                        key={image.src}
                        index={index}
                        {...image}
                        updateActiveImage={(index) => setActiveImage(index + 1)}
                    />
                ))}
            </div>
        </section>
    );
}
