import React, {useState} from 'react';
import "./style.scss";

const projects = [
    {
        
        title: "Object Detection",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged.",
        category:"Object Detection, Yolov5",
    },

    {
        
        title: "Image Classification",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged.",
        category:"Classification, Transfer Learning",
    },
    {
        
        title: "Project 3",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged.",
        category:"",
    },
    {
        
        title: "Project 4",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged.",
        category:"",
    },
];

function GalleryItem({category,description,title,updateActiveProject,index}){
    return (
        <div className="gallery-item-wrapper">
            <div className="gallery-item">
                <div className="gallery-item-info">
                    <h1 className="gallery-item-title">{title}</h1>
                    <h6 className="gallery-item-subtitle">{description}</h6>
                    <p className="gallery-item-category">{category}</p>
                
                </div>
                
            </div>
        </div>
    );
}

export default function Gallery() {
    const [activeProject,setActiveProject] = useState(1);
    return(
        <section className="gallery-wrap">
            <div className="gallery">
                <div className="gallery-counter">
                    <span>{activeProject}</span>
                    <span className="divider"/>
                    <span>{projects.length}</span>
 
                </div>
                {projects.map((project,index) => (
                    <GalleryItem
                        index={index}
                        {...projects}
                        updateActiveImage={(index) => setActiveProject(index + 1)}
                    />
                ))}
            </div>
        </section>
    );
}
