import React, {useState, useEffect,useRef} from 'react';
import "./style.scss";
import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';
import cn from 'classnames';


const projects = [
    {
        
        title: "Object Detection",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
        category:"Object Detection, Yolov5"
    },

    {
        
        title: "Image Classification",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
        category:"Classification, Transfer Learning"
    },
    {
        
        title: "Prediction",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry. ",
        category:"Regression, XGBoost"
    },
    {
        
        title: "Visualization",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
        category:"EDA, Visualization"
    },
];

function GalleryItem({category,description,title,updateActiveProject,index}){
    return (
        <div className="gallery-item-wrapper" data-scroll-section>  
            <div className="gallery-item">
                <div className="gallery-item-info">
                    <h1 className="gallery-item-title">{title}</h1>
                    <h6 className="gallery-item-description">{description}</h6>
                    <p className="gallery-item-category">{category}</p>
                </div> 
            </div>   
        </div>
    );
}

export default function Gallery() {
    const [activeProject,setActiveProject] = useState(1);
    const ref = useRef(null);
    useEffect(() => {
      const sections= gsap.utils.toArray('.gallery-item-wrapper');
      gsap.to(sections, {
          xPercent: -100 * (sections.length-1),
          ease: 'none',
          scrollTrigger: {
              start : 'top top',
              trigger: ref.current,
              scroll: '#main-container',
              pin: true,
              scrub: 0.5,
              span: 1/(sections.length-1),
              end: ()=>'+={ref.current.offsetWidth}'

          }
      })
    }, [])
    return(
        <section className="gallery-wrap" >
            <div className="gallery" ref= {ref}>
                <div className="gallery-counter">
                    <span>{activeProject}</span>
                    <span className="divider"/>
                    <span>{projects.length}</span>
                </div>
                {projects.map((project,index) => (
                    <GalleryItem
                        index={index}
                        {...project}
                        updateActiveProject={(index) => setActiveProject(index + 1)}
                    />
                ))}
            </div>
        </section>
    );
}
