const projects = [
    {
        
        title: "Object Detection",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
        category:"Object Detection, Yolov5"
    },

    {
        
        title: "Image Classification",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
        category:"Classification, Transfer Learning"
    },
    {
        
        title: "Prediction",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry. ",
        category:"Regression, XGBoost"
    },
    {
        
        title: "Visualization",
        description:"Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
        category:"EDA, Visualization"
    },
];

function GalleryItem({category,description,title,updateActiveProject,index}){
    return (
        <div className="gallery-item-wrapper" data-scroll-section>  
            <div className="gallery-item">
                <div className="gallery-item-info">
                    <h1 className="gallery-item-title">{title}</h1>
                    <h6 className="gallery-item-description">{description}</h6>
                    <p className="gallery-item-category">{category}</p>
                </div> 
            </div>   
        </div>
    );
}

