        <section className="gallery-wrap" >
            <div className="gallery">
                <div className="gallery-counter">
                    <span>{activeProject}</span>
                    <span className="divider"/>
                    <span>{projects.length}</span>
                </div>
                {projects.map((project,index) => (
                    <GalleryItem
